        private static Collection<ArInvoiceItems> _arInvoiceItems = new Collection<ArInvoiceItems>();
        public static Collection<ArInvoiceItems> ArInvoiceItems
        {
            get
            {
                if (_arInvoiceItems.Count == 0)
                {
                    GenerateDataContracts();
                }
                return _arInvoiceItems;
            }
        }

        static void GenerateDataContracts()
        {
            string[,] invoiceItemsData = GetArInvoiceItemData();
            int invoiceItemCount = invoiceItemsData.Length / 5;

            for (int i = 0; i < invoiceItemCount; i++)
            {
                string id = invoiceItemsData[i, 0].Trim();
                string invoiceId = invoiceItemsData[i, 1].Trim();
                string description = invoiceItemsData[i, 2].Trim();
                string code = invoiceItemsData[i, 3].Trim();
                decimal amount = decimal.Parse(invoiceItemsData[i, 4].Trim());

                ArInvoiceItems arInvoiceItem = new ArInvoiceItems()
                {
                    Recordkey = id,
                    InviInvoice = invoiceId,
                    InviDesc = description,
                    InviArCode = code,
                    InviExtChargeAmt = amount,
                    InviArCodeTaxDistrs = (id == "6") ? new List<string>() { "107" } : (id == "13235") ? new List<string>() { "108" } : null,
                    InviArPayPlans = (id == "6") ? new List<string>() { "1006"} : null
                };
                _arInvoiceItems.Add(arInvoiceItem);
            }
        }

